css:
.header {
    text-align: center; 
    padding-bottom: 20px;
}
.header h1 {
    font-size: 17px;
    margin:  0; 
    font-weight: 600;
}
.header h3 {
    font-size: 16px;
    color: #004080;
}
table {
    width: 100%;
    border-collapse: collapse; 
}
table, th, td {
    border: 1px solid #000;
}
th, td {
    padding: 8px;
    text-align: left;
}
.highlight {
    background-color: #8db4e2;
}
.physical-inspection-note {
    text-align: center;
    color: red;
    font-weight: bold;
    margin-top: 20px;
    margin-bottom: 50px;
    text-decoration: underline;
} 
.print-format td { 
    border: 2px solid #000;
    font-size:14px;
}
.print-format tr td:first-child{
    border-left: 0;
}
.print-format tr td:last-child{
    border-right: 0;
}
.print-format th {
    background-color: #8db4e2;
    color: #000;
    font-size:14px;
    border:2px solid #000;
} 
.basic_info th , .goods_deatil{  
    border-left: 0;
    border-right: 0;
}
.goods_deatil tr th:first-child{
    border-left: 0;
}
.goods_deatil tr th:last-child{
    border-right: 0;
}
.basic_info{
        border-bottom:0;
            border-left: 0;
    border-right: 0;
    }
 .basic_info tr:last-child td {
        border-bottom:0;
    }
    .br {
    border-bottom: 1px dashed #000;
}
.outer_section{
    border:2px solid #000;
    padding:10px;
    background:#8db4e2;
}
.inner_section{
    border:2px solid #000;
    background:#fff;
}



