css:
.text-bold {
  font-weight: bold;
} 
p{
    font-size:14px;
}
.letter-head-header{
    display:flex;
    justify-content: space-between;
    align-items: center; 
    margin-bottom:25px;
} 
.letter-head-header p{
    color:#0060ad;
}
.image_logo{
    width: 165px;
}
.image_logo img{
    width: 100%;
}
.address{
    max-width: 162px;
}
.letter-head-header .contact{
    border-left:1px solid #0060ad;
    padding:0px 10px;
    border-right:1px solid #0060ad; 
    margin: 0px 16px;
}
.artboard img{
    width: 130px; 
}
.print-format .value{
    line-height: unset;
    font-size: 14px;
}
.stamp{
    min-height:92px;
    width: 68%;
    margin: auto;
} 
.print-format p.text-end{
    color:#0060ad;
}
.page_footer{
    border-top:1px solid #0060ad;
} 
.page_footer p{
    color:#0060ad;
}
.page_footer .image_logo{
    margin-left:22px;
}
.print-format .row:not(.section-break){
    margin-top:6px !important;
} 
.page_title{
    font-size: 24px;
    font-weight: 600;
    color: #fff;
    background: #0060af;
    padding: 6px 12px;
    width: fit-content; 
    margin: auto;
}
.sub_title{
    font-size: 18px;
    font-weight: 600;
}

.value{
    border-bottom: 1px solid #000; 
}
.table {
  border-top: 1px solid #f4f5f6;
  font-size: 10px;
}
.del_property {
  background-color: #fff;
  font-size: 12px;
}
.del_property h4 {
  margin: 10px 0;
} 
.print-format .table-bordered{
        border: 1px solid #000;
}
.table>:not(:last-child)>:last-child>* {
    border-bottom-color: #000;
}
.print-format .table-bordered td, .print-format .table-bordered th {
    border: 1px solid #000;
    color:#000;
    font-size: 14px;
    padding:0px !important;
}

    .print-format .table-bordered td{
        font-size: 14px;
    padding:0px !important;
    }
.print-format{
    padding:20px 0px;
}
@media print {
    .print-format .table-bordered{
            border: 1px solid #000;
    }
    .table>:not(:last-child)>:last-child>* {
        border-bottom-color: #000;
    }
    .print-format .table-bordered td, .print-format .table-bordered th {
        border: 1px solid #000 !important;
        color:#000;
        font-size: 14px;
        padding:0px !important;
    } 
    .print-format .table-bordered td{
        font-size: 14px !important;
    padding:0px !important;
    }
  .p-l-0 {
    padding-left: 0 !important;
  }
  .p-r-0 {
    padding-right: 0 !important;
  }
  .del_property {
    font-size: 9px;
    line-height: 12px;
  }
  .del_property h4 {
    margin: 10px 0 5px 0;
    font-size: 12px;
    line-height: 14px;
  }
  .del_property .table {
    border: none;
  }
  .del_property .table td,
  .del_property .table th {
    font-size: 10px;
    line-height: 12px;
    padding: 2px 0px !important;
    border: none;
  }
  .del_property h4 {
    margin: 2px 0;
  }
  .logo_img {
    width: 100px !important;
    margin-left: 10px;
  }
  .logo_img_1 {
    width: 100px !important;
    margin-right: 11px !important;
  }
  .del_property .print-page {
    padding-top: 170px;
  }
  .letter-head-footer {
    position: fixed;
    bottom: 0px;
    left: 0;
    width: 100%;
    text-align: center;
  } 
  .col-md-1 {
    width:8% !important;
  } 
  .col-md-2 {
    width:16% !important;
  }
  .col-md-3{
    width:25% !important;
  } 
  .col-md-4{
    width:33% !important;
  }
  .col-md-5{
    width:41% !important;
  }
  .col-md-6{
    width:50% !important;
  }
  .col-md-7{
    width:58% !important;
  } 
  .col-md-8{
    width:66% !important;
  }
  .col-md-9{
    width:75% !important;
  }
  .col-md-10{
    width:83% !important;
  }
  .col-md-11{
    width:91% !important;
  }
  .col-md-12{
    width:100% !important;
  }
  .stamp{
        min-height:80px !important;
    }
    .page_footer .image_logo{
        margin-left:22px;
    }
    .page_title{ 
        width: 134px !important;
    }
    p{
    font-size:14px;
}
}
